import React, { useState, useEffect } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: 'Some Dummy Movie',
  //     openingText: 'This is the opening text of the movie',
  //     releaseDate: '2021-05-18',
  //   },
  //   {
  //     id: 2,
  //     title: 'Some Dummy Movie 2',
  //     openingText: 'This is the second opening text of the movie',
  //     releaseDate: '2021-05-19',
  //   },
  // ];

  const [movies, setMovies] = useState([]);

  // useEffect(() => {
  //   return console.log(movies);
  // }, [movies]);

  const fetchMoviesHandler = () => {
    const url = "https://swapi.dev/api/films";
    const updatedMovies = [];

    // .then() handles what happens after the information is fetched and .catch()
    // handles any potential errors.
    // .json transforms json into javascript code
    fetch(url)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        // data is now the json object with key-value pairs
        // the previous then() tranfromed it into js readable json
        console.log(data.results);

        data.results.map((result) => {
          const newMovie = {
            title: "",
            releaseDate: "",
            openingText: "",
          };

          newMovie.title = result.title;
          newMovie.releaseDate = result.release_date;
          newMovie.openingText = result.opening_crawl;

          updatedMovies.push(newMovie);
        });

        setMovies(updatedMovies);
        console.log(movies);
      });
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
